document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[data-id]").forEach(t=>{t.addEventListener("mouseenter",()=>{const d=t.getAttribute("data-id"),e=document.querySelector(`[desc-id="${d}"]`);e&&(e.style.display="block")}),t.addEventListener("mouseleave",()=>{const d=t.getAttribute("data-id"),e=document.querySelector(`[desc-id="${d}"]`);e&&(e.style.display="none")})})});console.log("javascript for detail works.......");
